import React, { useState, useEffect } from "react";
import { web3, rideSharingContract } from "../web3";

const DriverDashboard = () => {
  const [source, setSource] = useState("");
  const [destination, setDestination] = useState("");
  const [seats, setSeats] = useState("");
  const [amount, setAmount] = useState("");
  const [rides, setRides] = useState([]);
  const [statusMessage, setStatusMessage] = useState("");

  console.log("DriverDashboard loaded!");

  // Convert numeric status from the contract to text
  const getStatusText = (status) => {
    if (status === "0") return "Not Started";
    if (status === "1") return "Ongoing";
    if (status === "2") return "Completed";
    return "Unknown";
  };

  // Load all rides from the contract
  const loadRides = async () => {
    console.log("Inside loadRides in DriverDashboard");
    try {
      console.log("Requesting rideCount in DriverDashboard...");
      const rideCount = await rideSharingContract.methods.rideCount().call();
      console.log("rideCount from contract (DriverDashboard):", rideCount);

      let ridesArray = [];
      for (let i = 1; i <= rideCount; i++) {
        const ride = await rideSharingContract.methods.rides(i).call();
        ride.amountEth = web3.utils.fromWei(ride.amount, "ether");
        ridesArray.push(ride);
      }
      setRides(ridesArray);
    } catch (error) {
      console.error("Error in loadRides (DriverDashboard):", error);
      alert("Error in loadRides (DriverDashboard): " + error.message);
    }
  };

  useEffect(() => {
    loadRides();
  }, []);

  // Propose a custom ride based on user input
  const handleProposeRide = async () => {
    if (!source || !destination || !seats || !amount) {
      alert("Please fill in all fields!");
      return;
    }
    await proposeRide(source, destination, seats, amount);
  };

  // Propose a pre-filled dummy ride: Saskatoon â†’ Regina, 1 seat, 0.5 ETH
  const handleProposeDummyRide = async () => {
    await proposeRide("Saskatoon", "Regina", 1, "0.5");
  };

  // Helper function to propose a ride
  const proposeRide = async (_source, _destination, _seats, _amount) => {
    try {
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      const driver = accounts[0];
      const amountInWei = web3.utils.toWei(_amount, "ether");

      const receipt = await rideSharingContract.methods
        .proposeRide(_source, _destination, parseInt(_seats), amountInWei)
        .send({ from: driver });

      setStatusMessage("Ride proposed successfully!");

      // Attempt optimistic update
      if (receipt.events && receipt.events.RideProposed) {
        const event = receipt.events.RideProposed.returnValues;
        const newRide = {
          id: event.id,
          driver: event.driver,
          source: event.source,
          destination: event.destination,
          seats: event.seats,
          amount: event.amount,
          amountEth: web3.utils.fromWei(event.amount, "ether"),
          status: "0",
          user: "0x0000000000000000000000000000000000000000"
        };
        setRides((prev) => [...prev, newRide]);
      } else {
        // fallback
        setTimeout(loadRides, 2000);
      }

      // Clear the custom input fields if you used them
      setSource("");
      setDestination("");
      setSeats("");
      setAmount("");
    } catch (error) {
      console.error("Error proposing ride:", error);
      setStatusMessage("Error proposing ride.");
    }
  };

  // Complete an ongoing ride
  const handleCompleteRide = async (rideId) => {
    try {
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      await rideSharingContract.methods.completeRide(rideId).send({ from: accounts[0] });
      setStatusMessage("Ride completed successfully!");
      loadRides();
    } catch (error) {
      console.error("Error completing ride:", error);
      setStatusMessage("Error completing ride.");
    }
  };

  // Filter rides by status
  const proposedRides = rides.filter((ride) => ride.status === "0");
  const ongoingRides = rides.filter((ride) => ride.status === "1");
  const completedRides = rides.filter((ride) => ride.status === "2");

  return (
    <div style={{ padding: "20px" }}>
      <h2>Driver Dashboard</h2>

      {/* Button to propose the dummy ride */}
      <button onClick={handleProposeDummyRide} style={{ marginBottom: "10px" }}>
        Propose Dummy Ride (Saskatoon â†’ Regina, seats=1, 0.5 ETH)
      </button>

      <section style={{ border: "1px solid #ccc", padding: "1rem", marginBottom: "1rem" }}>
        <h3>Propose a Custom Ride</h3>
        <label>
          Source: <br />
          <input type="text" value={source} onChange={(e) => setSource(e.target.value)} />
        </label>
        <br />
        <label>
          Destination: <br />
          <input type="text" value={destination} onChange={(e) => setDestination(e.target.value)} />
        </label>
        <br />
        <label>
          Seats Available: <br />
          <input type="number" value={seats} onChange={(e) => setSeats(e.target.value)} />
        </label>
        <br />
        <label>
          Amount (ETH): <br />
          <input type="number" value={amount} onChange={(e) => setAmount(e.target.value)} />
        </label>
        <br />
        <button onClick={handleProposeRide}>Propose Ride</button>
        {statusMessage && <p>{statusMessage}</p>}
      </section>

      <section style={{ marginBottom: "1rem" }}>
        <h3>Rides Proposed (Not Started)</h3>
        {proposedRides.length === 0 ? (
          <p>No rides proposed.</p>
        ) : (
          <ul>
            {proposedRides.map((ride) => (
              <li key={ride.id}>
                <strong>Source:</strong> {ride.source} |{" "}
                <strong>Destination:</strong> {ride.destination} |{" "}
                <strong>Seats:</strong> {ride.seats} |{" "}
                <strong>Amount:</strong> {ride.amountEth} ETH |{" "}
                <strong>Status:</strong> {getStatusText(ride.status)}
              </li>
            ))}
          </ul>
        )}
      </section>

      <section style={{ marginBottom: "1rem" }}>
        <h3>Ongoing Rides</h3>
        {ongoingRides.length === 0 ? (
          <p>No ongoing rides.</p>
        ) : (
          <ul>
            {ongoingRides.map((ride) => (
              <li key={ride.id}>
                <strong>Source:</strong> {ride.source} |{" "}
                <strong>Destination:</strong> {ride.destination} |{" "}
                <strong>Seats:</strong> {ride.seats} |{" "}
                <strong>Amount:</strong> {ride.amountEth} ETH |{" "}
                <strong>Status:</strong> {getStatusText(ride.status)}{" "}
                <button onClick={() => handleCompleteRide(ride.id)}>Complete</button>
              </li>
            ))}
          </ul>
        )}
      </section>

      <section style={{ marginBottom: "1rem" }}>
        <h3>Completed Rides</h3>
        {completedRides.length === 0 ? (
          <p>No completed rides.</p>
        ) : (
          <ul>
            {completedRides.map((ride) => (
              <li key={ride.id}>
                <strong>Source:</strong> {ride.source} |{" "}
                <strong>Destination:</strong> {ride.destination} |{" "}
                <strong>Seats:</strong> {ride.seats} |{" "}
                <strong>Amount:</strong> {ride.amountEth} ETH |{" "}
                <strong>Status:</strong> {getStatusText(ride.status)}
              </li>
            ))}
          </ul>
        )}
      </section>

      <button onClick={loadRides}>Refresh Rides</button>
    </div>
  );
};

export default DriverDashboard;
