import React, { useState, useEffect } from "react";
import { web3, rideSharingContract } from "../web3";

function UserDashboard() {
  const [rides, setRides] = useState([]);
  const [statusMessage, setStatusMessage] = useState("");

  const getStatusText = (status) => {
    if (status === "0") return "Not Started";
    if (status === "1") return "Ongoing";
    if (status === "2") return "Completed";
    return "Unknown";
  };

  const loadRides = async () => {
    console.log("Inside loadRides in UserDashboard");
    try {
      // 1) Check the chain/network ID
      const chainId = await web3.eth.getChainId();
      console.log("Current chainId (UserDashboard):", chainId);

      console.log("Requesting rideCount in UserDashboard...");
      const rideCount = await rideSharingContract.methods.rideCount().call();
      console.log("rideCount from contract (UserDashboard):", rideCount);

      let ridesArray = [];
      for (let i = 1; i <= rideCount; i++) {
        const ride = await rideSharingContract.methods.rides(i).call();
        ride.amountEth = web3.utils.fromWei(ride.amount, "ether");
        ridesArray.push(ride);
      }
      setRides(ridesArray);
    } catch (error) {
      console.error("Error loading rides in UserDashboard:", error);
      alert("Error in loadRides (UserDashboard): " + error.message);
    }
  };

  useEffect(() => {
    loadRides();
    const interval = setInterval(loadRides, 2000); // poll every 2s
    return () => clearInterval(interval);
  }, []);

  const handleSelectRide = async (rideId, rideWei) => {
    try {
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      const user = accounts[0];
      await rideSharingContract.methods
        .selectRide(rideId)
        .send({ from: user, value: rideWei });
      setStatusMessage("Ride selected & paid successfully!");
      loadRides();
    } catch (error) {
      console.error("Error selecting ride:", error);
      setStatusMessage("Transaction failed or canceled.");
    }
  };

  const availableRides = rides.filter((ride) => ride.status === "0");

  return (
    <div style={{ padding: "20px" }}>
      <h2>User Dashboard - Available Rides</h2>
      <button onClick={loadRides}>Refresh Rides</button>
      {statusMessage && <p>{statusMessage}</p>}
      {availableRides.length === 0 ? (
        <p>No rides available at the moment.</p>
      ) : (
        <ul>
          {availableRides.map((ride) => (
            <li key={ride.id} style={{ marginBottom: "10px" }}>
              <div>
                <strong>Source:</strong> {ride.source} |{" "}
                <strong>Destination:</strong> {ride.destination} |{" "}
                <strong>Seats:</strong> {ride.seats} |{" "}
                <strong>Amount:</strong> {ride.amountEth} ETH |{" "}
                <strong>Status:</strong> {getStatusText(ride.status)}
              </div>
              {ride.status === "0" && (
                <button onClick={() => handleSelectRide(ride.id, ride.amount)}>
                  Select & Pay
                </button>
              )}
            </li>
          ))}
        </ul>
      )}
      {statusMessage && <p>{statusMessage}</p>}
    </div>
  );
}

export default UserDashboard;
