import Web3 from "web3";
import RideSharingABI from "./RideSharing.json";

let web3;
let rideSharingContract;

if (window.ethereum) {
  // MUST be a WebsocketProvider if you want .events support
  const wsProvider = new Web3.providers.WebsocketProvider("ws://127.0.0.1:7545");
  web3 = new Web3(wsProvider);

  const contractAddress = "0x795B54B52AE2C7E26d49033Ab788F917B3DFb236";
  rideSharingContract = new web3.eth.Contract(RideSharingABI.abi, contractAddress);
} else {
  alert("MetaMask not detected or no WebSocket provider!");
}

export { web3, rideSharingContract };
