import React, { useState, useEffect } from "react";

function DriverDashboard() {
  const [source, setSource] = useState("");
  const [destination, setDestination] = useState("");
  const [seats, setSeats] = useState("");
  const [amount, setAmount] = useState("");
  const [rides, setRides] = useState([]);
  const [statusMessage, setStatusMessage] = useState("");

  // On mount, load any rides from localStorage
  useEffect(() => {
    const storedRides = JSON.parse(localStorage.getItem("ecoRides")) || [];
    setRides(storedRides);
  }, []);

  // Save rides to localStorage + state
  const saveRides = (updatedRides) => {
    setRides(updatedRides);
    localStorage.setItem("ecoRides", JSON.stringify(updatedRides));
  };

  // Convert numeric status to text
  const getStatusText = (status) => {
    if (status === "NotStarted") return "Not Started";
    if (status === "Ongoing") return "Ongoing";
    if (status === "Completed") return "Completed";
    return "Unknown";
  };

  const handleProposeRide = () => {
    if (!source || !destination || !seats || !amount) {
      alert("Please fill in all fields!");
      return;
    }
    // Create a new ride object
    const newRide = {
      id: Date.now(),
      source,
      destination,
      seats,
      amount,
      status: "NotStarted"
    };
    const updatedRides = [...rides, newRide];
    saveRides(updatedRides);
    setStatusMessage("Ride proposed successfully!");
    setSource("");
    setDestination("");
    setSeats("");
    setAmount("");
  };

  // Complete an ongoing ride
  const handleCompleteRide = (rideId) => {
    const updatedRides = rides.map((ride) =>
      ride.id === rideId ? { ...ride, status: "Completed" } : ride
    );
    saveRides(updatedRides);
    setStatusMessage("Ride completed successfully!");
  };

  // Filter rides by status
  const proposedRides = rides.filter((ride) => ride.status === "NotStarted");
  const ongoingRides = rides.filter((ride) => ride.status === "Ongoing");
  const completedRides = rides.filter((ride) => ride.status === "Completed");

  return (
    <div style={{ padding: "20px" }}>
      <h2>Driver Dashboard</h2>

      <section style={{ border: "1px solid #ccc", padding: "1rem", marginBottom: "1rem" }}>
        <h3>Propose a Ride</h3>
        <label>
          Source: <br />
          <input
            type="text"
            value={source}
            onChange={(e) => setSource(e.target.value)}
          />
        </label>
        <br />
        <label>
          Destination: <br />
          <input
            type="text"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
          />
        </label>
        <br />
        <label>
          Seats Available: <br />
          <input
            type="number"
            value={seats}
            onChange={(e) => setSeats(e.target.value)}
          />
        </label>
        <br />
        <label>
          Amount (ETH): <br />
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
        </label>
        <br />
        <button onClick={handleProposeRide}>Propose Ride</button>
        {statusMessage && <p>{statusMessage}</p>}
      </section>

      <section style={{ marginBottom: "1rem" }}>
        <h3>Rides Proposed (Not Started)</h3>
        {proposedRides.length === 0 ? (
          <p>No rides proposed.</p>
        ) : (
          <ul>
            {proposedRides.map((ride) => (
              <li key={ride.id}>
                <strong>Source:</strong> {ride.source} |{" "}
                <strong>Destination:</strong> {ride.destination} |{" "}
                <strong>Seats:</strong> {ride.seats} |{" "}
                <strong>Amount:</strong> {ride.amount} ETH |{" "}
                <strong>Status:</strong> {getStatusText(ride.status)}
              </li>
            ))}
          </ul>
        )}
      </section>

      <section style={{ marginBottom: "1rem" }}>
        <h3>Ongoing Rides</h3>
        {ongoingRides.length === 0 ? (
          <p>No ongoing rides.</p>
        ) : (
          <ul>
            {ongoingRides.map((ride) => (
              <li key={ride.id}>
                <strong>Source:</strong> {ride.source} |{" "}
                <strong>Destination:</strong> {ride.destination} |{" "}
                <strong>Seats:</strong> {ride.seats} |{" "}
                <strong>Amount:</strong> {ride.amount} ETH |{" "}
                <strong>Status:</strong> {getStatusText(ride.status)}{" "}
                <button onClick={() => handleCompleteRide(ride.id)}>
                  Complete
                </button>
              </li>
            ))}
          </ul>
        )}
      </section>

      <section style={{ marginBottom: "1rem" }}>
        <h3>Completed Rides</h3>
        {completedRides.length === 0 ? (
          <p>No completed rides.</p>
        ) : (
          <ul>
            {completedRides.map((ride) => (
              <li key={ride.id}>
                <strong>Source:</strong> {ride.source} |{" "}
                <strong>Destination:</strong> {ride.destination} |{" "}
                <strong>Seats:</strong> {ride.seats} |{" "}
                <strong>Amount:</strong> {ride.amount} ETH |{" "}
                <strong>Status:</strong> {getStatusText(ride.status)}
              </li>
            ))}
          </ul>
        )}
      </section>

      <button onClick={() => setRides(JSON.parse(localStorage.getItem("ecoRides")) || [])}>
        Refresh Rides
      </button>
    </div>
  );
}

export default DriverDashboard;
